import React, { useState } from 'react';
import { BookOpen, Calendar, Clock } from 'lucide-react';

export default function JournalEntry({ vendorId, onSubmit }) {
  const [entry, setEntry] = useState({
    title: '',
    content: '',
    type: 'note',
  });

  const entryTypes = [
    { value: 'note', label: 'Note' },
    { value: 'meeting', label: 'Meeting' },
    { value: 'call', label: 'Call' },
    { value: 'email', label: 'Email' },
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      ...entry,
      vendorId,
      createdAt: new Date(),
    });
    setEntry({ title: '', content: '', type: 'note' });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-200">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold flex items-center">
          <BookOpen className="h-5 w-5 mr-2 text-blue-600" />
          New Journal Entry
        </h3>
        <div className="flex items-center text-sm text-gray-500">
          <Calendar className="h-4 w-4 mr-1" />
          {new Date().toLocaleDateString()}
          <Clock className="h-4 w-4 ml-4 mr-1" />
          {new Date().toLocaleTimeString()}
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Title
            </label>
            <input
              type="text"
              value={entry.title}
              onChange={(e) => setEntry({ ...entry, title: e.target.value })}
              className="w-full rounded-lg border border-gray-300 px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
              placeholder="Entry title"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Type
            </label>
            <select
              value={entry.type}
              onChange={(e) => setEntry({ ...entry, type: e.target.value })}
              className="w-full rounded-lg border border-gray-300 px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
            >
              {entryTypes.map((type) => (
                <option key={type.value} value={type.value}>
                  {type.label}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-1">
            Content
          </label>
          <textarea
            value={entry.content}
            onChange={(e) => setEntry({ ...entry, content: e.target.value })}
            rows={4}
            className="w-full rounded-lg border border-gray-300 px-3 py-2 focus:outline-none focus:ring-1 focus:ring-blue-500"
            placeholder="Enter your journal entry..."
            required
          />
        </div>

        <div className="flex justify-end space-x-3">
          <button
            type="button"
            onClick={() => setEntry({ title: '', content: '', type: 'note' })}
            className="px-4 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50"
          >
            Clear
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Save Entry
          </button>
        </div>
      </form>
    </div>
  );
}
