import React, { useState } from 'react';
import VendorForm from './components/VendorForm';
import QuoteUploader from './components/QuoteUploader';
import JournalEntry from './components/JournalEntry';

function App() {
  const [activeTab, setActiveTab] = useState('vendor');

  return (
    <div className="app">
      <header className="header">
        <h1>Vendor Management System</h1>
      </header>
      <div className="tabs-container">
        <button
          className={`tab ${activeTab === 'vendor' ? 'active' : ''}`}
          onClick={() => setActiveTab('vendor')}
        >
          Vendor Info
        </button>
        <button
          className={`tab ${activeTab === 'quotes' ? 'active' : ''}`}
          onClick={() => setActiveTab('quotes')}
        >
          Quotes
        </button>
        <button
          className={`tab ${activeTab === 'journal' ? 'active' : ''}`}
          onClick={() => setActiveTab('journal')}
        >
          Journal Entries
        </button>
      </div>
      <main className="content">
        {activeTab === 'vendor' && <VendorForm onSubmit={(data) => console.log(data)} />}
        {activeTab === 'quotes' && <QuoteUploader onUpload={(data) => console.log(data)} />}
        {activeTab === 'journal' && <JournalEntry onSubmit={(data) => console.log(data)} />}
      </main>
    </div>
  );
}

export default App;
