import React, { useState } from 'react';
import { Building2, FileText, BookOpen } from 'lucide-react';
import VendorForm from './components/VendorForm';
import QuoteUploader from './components/QuoteUploader';
import JournalEntry from './components/JournalEntry';
import toast, { Toaster } from 'react-hot-toast';

function App() {
  const [activeTab, setActiveTab] = useState('vendor');
  const [selectedVendor, setSelectedVendor] = useState<string | null>(null);

  const handleVendorSubmit = (data: any) => {
    console.log('Vendor data:', data);
    toast.success('Vendor information saved successfully!');
  };

  const handleQuoteUpload = (data: any) => {
    console.log('Quote data:', data);
    toast.success('Quote uploaded successfully!');
  };

  const handleJournalEntry = (data: any) => {
    console.log('Journal entry:', data);
    toast.success('Journal entry saved successfully!');
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Toaster position="top-right" />

      {/* Header */}
      <header className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <Building2 className="h-8 w-8 text-blue-600" />
              <h1 className="ml-2 text-xl font-bold text-gray-900">
                Vendor Management System
              </h1>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Tabs */}
        <div className="border-b border-gray-200 mb-6">
          <nav className="-mb-px flex space-x-8">
            <button
              onClick={() => setActiveTab('vendor')}
              className={`pb-4 px-1 ${
                activeTab === 'vendor'
                  ? 'border-b-2 border-blue-500 text-blue-600'
                  : 'text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } font-medium flex items-center`}
            >
              <Building2 className="h-5 w-5 mr-2" />
              Vendor Information
            </button>
            <button
              onClick={() => setActiveTab('quotes')}
              className={`pb-4 px-1 ${
                activeTab === 'quotes'
                  ? 'border-b-2 border-blue-500 text-blue-600'
                  : 'text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } font-medium flex items-center`}
            >
              <FileText className="h-5 w-5 mr-2" />
              Quotes
            </button>
            <button
              onClick={() => setActiveTab('journal')}
              className={`pb-4 px-1 ${
                activeTab === 'journal'
                  ? 'border-b-2 border-blue-500 text-blue-600'
                  : 'text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } font-medium flex items-center`}
            >
              <BookOpen className="h-5 w-5 mr-2" />
              Journal Entries
            </button>
          </nav>
        </div>

        {/* Content */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          {activeTab === 'vendor' && (
            <VendorForm onSubmit={handleVendorSubmit} />
          )}
          {activeTab === 'quotes' && (
            <QuoteUploader
              vendorId={selectedVendor || 'temp-id'}
              onUpload={handleQuoteUpload}
            />
          )}
          {activeTab === 'journal' && (
            <JournalEntry
              vendorId={selectedVendor || 'temp-id'}
              onSubmit={handleJournalEntry}
            />
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
